import { getCartProducts } from "../services/cart";
import { useEffect, useReducer, useState } from "react";
import styled from "styled-components";
import CartProductsStyled from "../assets/cart-products/index.js";
import StyledLink from "../assets/link/index.js";

const CartContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    gap: 2rem;
    margin: 2rem;
    min-height: 92vh;
    overflow: hidden;
`

const CartEmptyContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-top: 2vh;
    gap: 1.5rem;
    min-height: 90vh;
    overflow: hidden;

    & button{
        color: white;
        background-color: rgba(109, 0, 156, 0.5);
        border: none;
        font-weight: bold;
        border-radius: .5rem;
        padding: 1.5rem;
        transition: all .7s;

        &:hover {
            background: linear-gradient(315deg, rgba(46,0,78,0.5) 30%, rgba(125,0,180,0.5) 100%);
            cursor: pointer;
        }
    }
`

const CartEmptyText = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    & h1 {
        color: white;
    }

    & p {
        font-size: .8rem;
        color: white;
    }
`

const ProductCard = styled.div`
    display: flex;
    flex-direction: column;
    gap: 2rem;
    width: 65%;
`

const Checkout = styled.div`
    display: flex;
    flex-direction: column;
    background-color: rgba(46,0,78,0.5);
    padding: 2rem;
    width: 25rem;
    max-height: 25rem;
    border-radius: .5rem;
    gap: .5rem;

    &:hover {
        box-shadow: 1px 0px 5px 5px rgba(89, 0, 161, 0.2);
    }
`

const CheckoutTitle = styled.h1`
    font-size: 1.5rem;
    color: white;
`

const SummaryContainer = styled.div`
    display: flex;
    flex-direction: column;
    gap: .5rem;

    & div{
        display: flex;
        flex-direction: row;
        justify-content: space-between;
        color: white;
    }
`

const TotalPrices = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    border: 1px solid white;
    padding: .5rem;
    border-radius: .5rem;
    color: white;
`

function CartProducts() {
    const [ cartProducts, setCartProducts ] = useState([]);

    async function fetchCartProducts() {
        const cartProductsAPI = await getCartProducts();
        setCartProducts(cartProductsAPI);
    }
    useEffect(() => {
        fetchCartProducts();
    }, []);

    function handleDeleteProduct(productId) {
        setCartProducts((prevProducts) =>
            prevProducts.filter((product) => product.id !== productId)
        );
    }

    const totalPrice = cartProducts.reduce((total, product) => total + Number(product.price), 0)
    const totalNewPrice = cartProducts.reduce((total, product) => total + Number(product.newprice), 0)
    const totalDiscont = parseFloat(totalPrice - totalNewPrice)

    const images = require.context('../assets/products-images', false, /\.(png|jpe?g|gif)$/)
    
    if(cartProducts.length < 1) {
        return (
            <CartEmptyContainer>
                <CartEmptyText>
                    <h1>Seu carrinho está vazio.</h1>
                    <p>Adicione algum produto para finalizar a compra</p>
                </CartEmptyText>

                <StyledLink to={"/"}>
                    <button>
                        VOLTE A COMPRAR
                    </button>
                </StyledLink>

            </CartEmptyContainer>
        )
    }
    else{
        return (
            <CartContainer>
                <ProductCard>
                    {cartProducts.map(product => {
                        const imagePath = `./${product.src}.png`;
                
                        const image = images(imagePath);

                        return (
                            <CartProductsStyled key={product.id} name={product.name} image={image} price={product.price} 
                            newprice={product.newprice} id={product.id} src={product.src} onDelete={handleDeleteProduct}/>
                        )
                    })}
                </ProductCard>

                <Checkout>
                    <CheckoutTitle>RESUMO</CheckoutTitle>

                    <SummaryContainer>
                        <div>
                            <p>Total do produtos:</p>
                            <p>R$ {parseFloat(totalPrice.toFixed(2)).toLocaleString('pt-BR')}</p>
                        </div>

                        <div>
                            <p>Frete:</p>
                            <p>R$ 0,00</p>
                        </div>
                    </SummaryContainer>

                    <TotalPrices>
                        <div>
                            <p>Total à vista:</p>
                            <p>R$ {parseFloat(totalNewPrice.toFixed(2)).toLocaleString('pt-BR')}</p>
                        </div>
                        <p>Desconto: {parseFloat(totalDiscont.toFixed(2)).toLocaleString('pt-BR')}</p>
                    </TotalPrices>
                </Checkout>

            </CartContainer>


        );  
    }
}

export default CartProducts;
