import styled from "styled-components";

const FooterContainer = styled.footer`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
`

const FooterNewsContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    gap: 1rem;
    width: 100%;
    background-color: rgba(46,0,78,0.5);
`

const FooterNewsTitle = styled.h3`
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
    font-size: 1.2rem;
    color: white;
`

const FooterNewsForm = styled.form`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    gap: 1rem;
`

const FooterNewsEmail = styled.input`
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    background-color: rgba(109, 0, 156, 0.5);;
    border: none;
    border-radius: .5rem;
    color: white;
    fonte-weight: bold;
    padding: .2rem;

     {
        outline: none;
    }
`

const FooterText = styled.p`
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
    font-size: 1rem;
`


function Footer(){
    return(
        <FooterContainer>
            <FooterNewsContainer>
                <FooterNewsTitle>Receba ofertas</FooterNewsTitle>
                <FooterNewsForm>
                    <FooterNewsEmail type="email" placeholder="Email" />
                    <button type="submit">Inscrever-se</button>
                </FooterNewsForm>
            </FooterNewsContainer>

            <FooterText>Copyright Â© 2024 SevenShop</FooterText>
        </FooterContainer>
    )
}

export default Footer;
