import { deleteCartProduct } from "../../services/cart.js";
import styled from "styled-components"
import remCartImg from "../images/trash.png"
import arrowLeft from "../images/arrow-left.png"
import arrowRight from "../images/arrow-right.png"

const ProductsList = styled.ol`
    display: flex;
    flex-direction: column;
    justify-content: center;
    list-style: none;
    border-radius: .5rem;
`

const Product = styled.li`
    display: flex;
    flex-direction: row;
    align-items: center;
    gap: 2rem;
    background-color: rgba(46,0,78,0.5);
    padding: 2rem;
    border-radius: .5rem;
    transition: all .7s;

    &:hover {
        box-shadow: 1px 0px 5px 5px rgba(89, 0, 161, 0.2);
    }
`

const DescriptionContainer = styled.div`
    display: flex;
    flex-direction: column;
    line-height: 1.25rem;
    text-align: left;
    color: white;
    gap: 1.5rem;
    width: 100%;
`

const Description = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
`

const ProductName = styled.h4`
    display: -webkit-box;
    overflow: hidden;
    text-overflow: ellipsis;
    -webkit-line-clamp: 2;
    -webkit-box-orient: horizontal;
`

const InfosContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    gap: .8rem;
`

const PriceContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    gap: .1rem;
`

const ProductPrice = styled.h3`
    font-size: .8rem;
    color: rgb(214, 214, 214);
`

const ProductNewPrice = styled.h3`
    font-size: .8rem;
    color: rgb(214, 214, 214);
`

const QuantityContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 1rem;
`

const QuantityMain = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: .5rem;
`

const QuantityInput = styled.input`
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    background-color: transparent;
    border: none;
    color: white;
    fonte-weight: bold;
    padding: .2rem;
    width: 1.5rem;

    &::-webkit-inner-spin-button, &::-webkit-outer-spin-button {
        -webkit-appearance: none;
        margin: 0;
    }

    &:focus {
        outline: 1px solid rgb(0, 183, 255);
        border-radius: .5rem;
    }
`

const QuantityContent = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    gap: .5rem;

    & button{
        display: flex;
        background-color: transparent;
        border: none;
        cursor: pointer;

        & img {
            width: 1rem;
        }
    }
`

const CartRemContainer = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: .5rem;
    text-align: center;
    border-radius: .5rem;
    width: 100%;
    transition: all .7s;
`

const ProductImg = styled.img`
    max-height: 10rem;
    max-width: 10rem;
`

const ButtonCartContainer = styled.button`
    display: flex;
    justify-content: center;
    border: none;
    align-items: center;
    background-color: transparent;
    width: 100%;
    border-radius: .5rem;
    height: 2rem;
    transition: all .7s;
    cursor: pointer;
`

const ButtonCart = styled.img`
    width: 2rem;
`

function CartProductsStyled({name, image, price, newprice, src, id, quantity, onQuantityChange, onDelete}) {

    function handleQuantityChange(e) {
        const newQuantity = Math.max(1, Math.min(25, Number(e.target.value)));
        onQuantityChange(id, newQuantity);
    }

    async function delCartProduct(productId) {
        await deleteCartProduct(productId);
        onDelete(productId);
    }

    const priceConvert = parseFloat(price)
    const newPriceConvert = parseFloat(newprice)

    function arrowL(){
        if(quantity > 1){
            onQuantityChange(id, Number(quantity) - 1);
        }
        else{
            delCartProduct(id);
        }
    }

    function arrowR(){
        if(quantity < 25){
            onQuantityChange(id, Number(quantity) + 1);
        }
        else{
            alert("Você pode não pode comprar mais de 25 produtos por vez!")
        }
    }

    return(
        <ProductsList>
            <h1>PRODUTOS</h1>

            <Product key={id}> 

                <ProductImg src={image} alt={src}/>

                <DescriptionContainer>
                    <Description>

                        <InfosContainer>
                            <ProductName>
                                {name}
                            </ProductName>

                            <PriceContainer>
                                <ProductNewPrice>
                                    A vista com desconto: R${newPriceConvert.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
                                </ProductNewPrice>
                                <ProductPrice>
                                    Parcelado em até 10x: R${priceConvert.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
                                </ProductPrice>
                            </PriceContainer>
                        </InfosContainer>

                        <QuantityContainer>
                            <QuantityMain>
                                <label>Qtd</label>
                                <QuantityContent>
                                    <button onClick={() => {arrowL()}}>
                                        <img src={arrowLeft} alt="arrow-left"/>
                                    </button>
                                    <QuantityInput type="number" defaultValue="1" value={quantity} onChange={handleQuantityChange} min="1" max="25"/>
                                    <button onClick={() => {arrowR()}}>
                                        <img src={arrowRight} alt="arrow-right"/>
                                    </button>
                                </QuantityContent>
                            </QuantityMain>

                            <CartRemContainer>
                                <ButtonCartContainer onClick={() => {
                                    delCartProduct(id)
                                }}>
                                    <ButtonCart src={remCartImg} alt="Cart Icon"/>
                                </ButtonCartContainer>
                            </CartRemContainer>
                        </QuantityContainer>
                    </Description>


                </DescriptionContainer>

            </Product>
        </ProductsList>
    )
}

export default CartProductsStyled
