import { Link } from "react-router-dom";
import { getCartProducts, patchCartProducts } from "../services/cart";
import { useEffect, useState } from "react";
import styled from "styled-components";
import CartProductsStyled from "../assets/cart-products/index.js";
import Footer from "../assets/footer/index.js";
import StyledLink from "../assets/link/index.js";

const CartContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    min-height: 92vh;
`

const CartContent = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    margin: 2vh;
    gap: 2rem;  
    overflow: hidden;

    @media screen and (min-width: 1080px){
        margin: 4vh;
    }
`

const CartEmptyContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
    margin-top: 2vh;
    gap: 1.5rem;
    min-height: 90vh;
    overflow: hidden;

    & button{
        color: white;
        background-color: rgba(109, 0, 156, 0.5);
        border: none;
        font-weight: bold;
        border-radius: .5rem;
        padding: 1.5rem;
        transition: all .7s;

        &:hover {
            background: linear-gradient(315deg, rgba(46,0,78,0.5) 30%, rgba(125,0,180,0.5) 100%);
            cursor: pointer;
        }
    }
`

const CardEmptyContent = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 1rem;
`

const CartEmptyText = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    & h1 {
        color: white;
        text-align: center;
    }

    & p {
        font-size: .8rem;
        text-align: center;
        color: white;
    }
`

const ProductCard = styled.div`
    display: flex;
    flex-direction: column;
    gap: 2rem;
    color: white;
    text-align: center;
`

const Checkout = styled.div`
    display: none;
    flex-direction: column;
    width: 20%;
    gap: 2rem;

    @media screen and (min-width: 1720px){
        display: flex;
    }
`

const CheckoutTitle = styled.h1`
    text-align: center;
    color: white;
`

const SummaryContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    background-color: rgba(46,0,78,0.5);
    padding: 2rem;
    border-radius: .5rem;
    gap: 4rem;

    &:hover {
        box-shadow: 1px 0px 5px 5px rgba(89, 0, 161, 0.2);
    }
`

const ProductsTotal = styled.div`
    display: flex;
    flex-direction: column;
    gap: 1rem;
    justify-content: space-between;

    & div {
        display: flex;
        flex-direction: row;
        padding: .5rem;
        justify-content: space-between;
        border-bottom: 1px solid white;
        color: white;
    }
`

const TotalPrices = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    gap: .5rem;
    text-align: right;
    color: white;
`

const SubtitleText = styled.p`
    display: flex;
    justify-content: center;
    color: white;
`

const Value = styled.p`
    color: rgb(0, 183, 255);
`

const TotalInTime = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    background-color: rgba(109, 0, 156, 0.5);
    padding: .5rem;
    border-radius: .5rem;
    gap: .5rem;
`

const ValueInTime = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    color: white;
`

const Terms = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: right;
    gap: .5rem;
    font-size: .8rem;
`

const Total1x = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    background-color: rgba(109, 0, 156, 0.5);
    padding: .5rem;
    border-radius: .5rem;
    gap: .5rem;
`

const Value1x = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
`

const Discont = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: right;
    gap: .5rem;
    font-size: .8rem;
`

const CheckoutButtonContainer = styled.div`
    display: flex;
    flex-direction: column;
    background-color: rgba(46,0,78,0.5);
    padding: 2rem;
    border-radius: .5rem;
    justify-content: center;
    gap: 1rem;
`

const CheckoutButton = styled.button`
    font-family: 'Poppins', sans-serif;
    color: white;
    background-color: rgba(109, 0, 156, 0.5);
    border: none;
    font-weight: bold;
    width: 100%;
    border-radius: .5rem;
    padding: 1.5rem;
    transition: all .7s;
    cursor: pointer;

    &:hover {
        background: linear-gradient(315deg, rgba(46,0,78,0.5) 30%, rgba(125,0,180,0.5) 100%);
    }
`

const ReturnButton = styled.button`
    font-family: 'Poppins', sans-serif;
    background-color: transparent;
    color: white;
    border: 1px solid rgba(109, 0, 156, 0.5);
    font-weight: bold;
    width: 100%;
    border-radius: .5rem;
    padding: 1.5rem;
    transition: all .7s;
    cursor: pointer;

    &:hover {
        background-color: rgba(109, 0, 156, 0.5);
    }
`

const ButtonLink = styled(Link)`
    text-decoration: none;
    color: white;
`

const BuyButtonContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: rgb(109, 0, 156);
    padding: 1rem;
    gap: 1rem;
    position: sticky;
    bottom: 0;

    & .buy-button-link{
        display: flex;
        justify-content: center;
        align-items: center;
        width: 100%;
    }

    @media screen and (min-width: 1720px){
        display: none;
    }

    @media screen and (min-width: 600px){
        & .buy-button-link{
            width: 90%;
        }
    }
`

const BuyResumeContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 1.5rem;
    width: 100%;

    @media screen and (min-width: 600px){
        width: 90%;
    }
`

const BuyResume = styled.div`
    display: flex;
    flex-direction: row;
    color: white;
    width: 100%;
    justify-content: space-between;
    align-items: center;
    font-family: 'Poppins', sans-serif;

    & h3{
        font-weight: bold; 
    }
`

const BuyResumeValue = styled.div`
    display: flex;
    flex-direction: row;
    gap: .5rem;
`

const BuyResumeDescription = styled.div`
    display: flex;
    flex-direction: column;
    color: white;
    width: 100%;
    justify-content: space-between;
    font-family: 'Poppins', sans-serif;
`

const BuyResumeInfo = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    padding: 0.75rem 0.25rem;
    border-bottom: 1px solid white;
`

const BuyResumePrices = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    padding: .5rem 0;
    gap: .5rem;
    width: 100%;
`

const BuyResumePrice = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    padding: 0.75rem 0.25rem;
    border-radius: .5rem;
    background-color: rgba(46, 0, 78, 0.5);
    width: 100%;
`

const PriceTerms = styled.div`
    display: flex;  
    flex-direction: column;
    text-align: right;
`

const TermsText = styled.p`
    font-size: .8rem;
`

const BuyButton = styled.button`
    font-family: 'Poppins', sans-serif;
    border-radius: .5rem;
    width: 100%;
    color: white;
    background-color: rgb(46,0,78);
    border: none;
    padding: 1rem;
`

function CartProducts() {
    const [cartProducts, setCartProducts] = useState([]);
    const [quantities, setQuantities] = useState({});

    async function fetchCartProducts() {
        const cartProductsAPI = await getCartProducts();
        setCartProducts(cartProductsAPI);

        const initialQuantities = {};
        cartProductsAPI.forEach((product) => {
            initialQuantities[product.id] = product.quantity;
        });
        setQuantities(initialQuantities);
    }

    useEffect(() => {
        fetchCartProducts();
    }, []);

    function handleDeleteProduct(productId) {
        setCartProducts((prevProducts) =>
            prevProducts.filter((product) => product.id !== productId)
        );

        if(cartProducts.length === 1){
            window.location.replace("/");
        }

        setQuantities((prevQuantities) => {
            const updatedQuantities = { ...prevQuantities };
            delete updatedQuantities[productId];
            return updatedQuantities;
        });
    }

    function handleQuantityChange(productId, newQuantity) {
        setQuantities((prevQuantities) => ({
            ...prevQuantities, [productId]: newQuantity,
        }));

        patchCartProducts(productId, { quantity: newQuantity })
    }

    const totalPrice = cartProducts.reduce((total, product) => {
        const quantity = quantities[product.id] || 1;
        return total + parseFloat(product.price) * quantity;
    }, 0);

    const totalNewPrice = cartProducts.reduce((total, product) => {
        const quantity = quantities[product.id] || 1;
        return total + parseFloat(product.newprice) * quantity;
    }, 0);

    const totalDiscont = totalPrice - totalNewPrice;
    const frete = 32.9;

    const images = require.context('../assets/products-images', false, /\.(png|jpe?g|gif)$/);

    if (cartProducts.length < 1) {
        return (
            <CartEmptyContainer>
                <CardEmptyContent>
                    <CartEmptyText>
                        <h1>Seu carrinho está vazio.</h1>
                        <p>Adicione algum produto para finalizar a compra</p>
                    </CartEmptyText>
                    <StyledLink to={"/"}>
                        <button>VOLTE A COMPRAR</button>
                    </StyledLink>
                </CardEmptyContent>
                <Footer />
            </CartEmptyContainer>
        );
    } else {
        return (
            <CartContainer>
                <CartContent>
                    <ProductCard>
                        <h1>PRODUTOS</h1>
                        {cartProducts.map((product) => {
                            const imagePath = `./${product.src}.png`;
                            const image = images(imagePath);

                            return (
                                <CartProductsStyled
                                    key={product.id}
                                    name={product.name}
                                    image={image}
                                    price={product.price}
                                    newprice={product.newprice}
                                    id={product.id}
                                    src={product.src}
                                    quantity={quantities[product.id] || 1}
                                    onQuantityChange={handleQuantityChange}
                                    onDelete={handleDeleteProduct}
                                />
                            );
                        })}
                    </ProductCard>

                    <Checkout>
                        <CheckoutTitle>RESUMO</CheckoutTitle>
                        <SummaryContainer>
                            <ProductsTotal>
                                <div>
                                    <SubtitleText>Total do produtos:</SubtitleText>
                                    <Value>R$ {totalPrice.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</Value>
                                </div>
                                <div>
                                    <SubtitleText>Frete:</SubtitleText>
                                    <Value>R$ {frete.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</Value>
                                </div>
                            </ProductsTotal>
                            <TotalPrices>
                                <TotalInTime>
                                    <ValueInTime>
                                        <SubtitleText>Total à prazo:</SubtitleText>
                                        <Value>R$ {(totalPrice + frete).toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</Value>
                                    </ValueInTime>
                                    <Terms>
                                        <p>(Até 10x sem juros)</p>
                                    </Terms>
                                </TotalInTime>
                                <Total1x>
                                    <Value1x>
                                        <SubtitleText>Total à vista:</SubtitleText>
                                        <Value>R$ {(totalNewPrice + frete).toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</Value>
                                    </Value1x>
                                    <PriceTerms>
                                        <Terms><p>(Economize: <strong>R$ {totalDiscont.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</strong>)</p></Terms>
                                    </PriceTerms>
                                </Total1x>
                            </TotalPrices>
                        </SummaryContainer>

                        <CheckoutButtonContainer>
                            <ButtonLink to="/compra">
                                <CheckoutButton>FINALIZAR COMPRA</CheckoutButton>
                            </ButtonLink>
                            <ButtonLink to="/">
                                <ReturnButton>VOLTE A COMPRAR</ReturnButton>
                            </ButtonLink>
                        </CheckoutButtonContainer>
                    </Checkout>

                </CartContent>                      

                <BuyButtonContainer>
                    <BuyResumeContainer>
                        <BuyResume>
                            <h3>RESUMO</h3>
                            <BuyResumeValue>
                                <p>VALOR À VISTA:</p>
                                <Value>
                                    <strong>
                                        R$ {totalNewPrice.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
                                    </strong>
                                </Value>
                            </BuyResumeValue>
                        </BuyResume>

                        <BuyResumeDescription>

                            <BuyResumeInfo>
                                <p>Valor total:</p>
                                <Value><strong>R$ {totalPrice.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</strong></Value>
                            </BuyResumeInfo>
                            <BuyResumeInfo>
                                <p>Frete:</p>
                                <Value><strong>R$ {frete.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</strong></Value>
                            </BuyResumeInfo>

                        </BuyResumeDescription>

                        <BuyResumePrices>
                                <BuyResumePrice>
                                    <p>Total à prazo:</p>
                                    <PriceTerms>
                                        <Value><strong>R$ {(totalPrice + frete).toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</strong></Value>
                                        <Terms>
                                            <TermsText>
                                                (Até <strong>10x</strong> de <strong>{((totalPrice + frete) / 10).toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</strong> sem juros)
                                            </TermsText>
                                        </Terms>
                                    </PriceTerms>
                                </BuyResumePrice>

                                <BuyResumePrice>
                                    <p>Total à vista:</p>
                                    <PriceTerms>
                                        <Value><strong>R$ {(totalNewPrice + frete).toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</strong></Value>
                                        <Terms>
                                            <TermsText>
                                                (Economize: <strong>R$ {totalDiscont.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</strong>)
                                            </TermsText>
                                        </Terms>
                                    </PriceTerms>
                                </BuyResumePrice>
                            </BuyResumePrices>

                    </BuyResumeContainer>
                    <ButtonLink className="buy-button-link" to="/compra">
                        <BuyButton>FINALIZAR COMPRA</BuyButton>             
                    </ButtonLink>
                </BuyButtonContainer>
                <Footer display="none" />
             </CartContainer>
        );
    }
}

export default CartProducts;
