import styled from "styled-components";
import emailIcon from "../images/email-icon.png"

const FooterContainer = styled.footer`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: rgba(46,0,78,0.5);
    width: 100%;
`

const FooterNewsContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    padding: 1rem;
    align-items: center;
    gap: 1rem;
    height: 50%;
    width: 100%;
`

const FooterNewsTitle = styled.h3`
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
    font-size: 1.2rem;
    color: white;
`

const FooterNewsForm = styled.form`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    height: 100%;
    gap: 1rem;
`

const FooterNewsEmailContainer = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    padding: .5rem;
    background-color: rgba(109, 0, 156, 0.5);
    border-radius: .5rem;
`

const FooterNewsEmail = styled.input`
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: transparent;
    border: none;
    height: 100%;
    width: 20rem;
    color: white;
    fonte-weight: bold;
    padding: .5rem;

    &::placeholder {
        color: white;
    }

    &:focus-visible {
        outline: none;
    }
`

const EmailImg = styled.img`
    height: 1.2rem;
    width: 1.2rem;
`

const FooterNewsButton = styled.button`
    display: flex;
    justify-content: center;
    height: 100%;
    align-items: center;
    font-weight: bold;
    background-color: rgba(109, 0, 156, 0.5);;
    border: none;
    border-radius: .5rem;
    color: white;
    padding: 1rem;
    cursor: pointer;

    &:hover {
        background: linear-gradient(315deg, rgba(46,0,78,0.5) 30%, rgba(125,0,180,0.5) 100%);
    }
`

const FooterText = styled.h1`
    font-family: 'Dancing Script', sans-serif;
    padding: 1rem;
    color: white;
    font-weight: bold;
    font-size: 1.2rem;
`


function Footer(){
    return(
        <FooterContainer>
            <FooterText>Copyright Â© 2024 SevenShop</FooterText>
        </FooterContainer>
    )
}

export default Footer;
