import { deleteCartProduct } from "../../services/cart.js";
import styled from "styled-components"
import StyledLink from "../link/index.js"
import remCartImg from "../images/rem-to-cart.png"
import arrowLeft from "../images/arrow-left.png"
import arrowRight from "../images/arrow-right.png"

const ProductsList = styled.ol`
    display: flex;
    flex-direction: column;
    justify-content: center;
    list-style: none;
    border-radius: .5rem;
`

const Product = styled.li`
    display: flex;
    flex-direction: row;
    align-items: center;
    gap: 2rem;
    background-color: rgba(46,0,78,0.5);
    padding: 2rem;
    border-radius: .5rem;
    transition: all .7s;

    &:hover {
        box-shadow: 1px 0px 5px 5px rgba(89, 0, 161, 0.2);
    }
`

const DescriptionContainer = styled.div`
    display: flex;
    flex-direction: column;
    line-height: 1.25rem;
    text-align: left;
    color: white;
    gap: 1.5rem;
    width: 100%;
`

const Description = styled.div`
    display: flex;
    flex-direction: column;
    gap: .8rem;
`

const ProductName = styled.h4`
    display: -webkit-box;
    overflow: hidden;
    text-overflow: ellipsis;
    -webkit-line-clamp: 2;
    -webkit-box-orient: horizontal;
`

const InfosContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
`

const PriceContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    gap: .1rem;
`

const ProductPrice = styled.h5`
    color: rgb(214, 214, 214);
    text-decoration: line-through;
`

const ProductNewPrice = styled.h3`
    font-size: 1.3rem;
    color: rgb(0, 183, 255);
`

const QuantityContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: .5rem;
`

const QuantityInput = styled.input`
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    background-color: transparent;
    border: none;
    color: white;
    fonte-weight: bold;
    padding: .2rem;
    width: 2rem;

    &::-webkit-inner-spin-button, &::-webkit-outer-spin-button {
        -webkit-appearance: none;
        margin: 0;
    }

    &:focus {
        outline: 1px solid rgb(0, 183, 255);
        border-radius: .5rem;
    }
`

const QuantityContent = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    gap: .5rem;

    & button{
        isplay: flex;
        background-color: transparent;
        border: none;

        & img {
            width: 1rem;
        }
    }
`

const ArrowLeftButton = styled.button`

`

const ArrowRightButton = styled.img`
    
`

const ProductBuyContainer = styled.div`
    display: flex;
    justify-content: center;
    gap: .5rem;
    align-items: center;
    border-radius: .5rem;
    text-align: center;
    border-radius: .5rem;
    width: 100%;
    transition: all .7s;
`

const ProductBuyButton = styled.button`
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: rgba(109, 0, 156, 0.5);
    font-family: Poppins, sans-serif;
    font-size: 1rem;
    height: 3rem; 
    font-weight: bold;
    border: none;
    width: 80%;
    border-radius: .5rem;
    transition: all .7s;

    &:hover {
        background: linear-gradient(315deg, rgba(46,0,78,0.5) 30%, rgba(125,0,180,0.5) 100%);
        cursor: pointer;
    }
`

const ProductImg = styled.img`
    max-height: 10rem;
    max-width: 10rem;
`

const ButtonCartContainer = styled.button`
    display: flex;
    justify-content: center;
    border: none;
    align-items: center;
    background-color: rgba(109, 0, 156, 0.5);
    width: 20%;
    border-radius: .5rem;
    height: 3rem;
    transition: all .7s;

    &:hover {
        background: linear-gradient(315deg, rgba(46,0,78,0.5) 30%, rgba(125,0,180,0.5) 100%);
        cursor: pointer;
    }
`

const ButtonCart = styled.img`
    width: 1.5rem;
`

function CartProductsStyled({name, image, price, newprice, src, id, onDelete}) {

    async function delCartProduct(productId) {
        await deleteCartProduct(productId);
        onDelete(productId);
    }

    const priceConvert = parseFloat(price)
    const newPriceConvert = parseFloat(newprice)

    return(
        <ProductsList>
            <Product key={id}> 

                <ProductImg src={image} alt={src}/>

                <DescriptionContainer>
                    <Description>
                        <ProductName>
                            {name}
                        </ProductName>

                        <InfosContainer>

                            <PriceContainer>
                                <ProductPrice>
                                    R${priceConvert.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
                                </ProductPrice>

                                <ProductNewPrice>
                                    R${newPriceConvert.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
                                </ProductNewPrice>

                                <h6>
                                    √Å vista
                                </h6>
                            </PriceContainer>

                            <QuantityContainer>
                                <label>Qtd</label>
                                <QuantityContent>
                                    <ArrowLeftButton>
                                        <img src={arrowLeft} alt="arrow-left"/>
                                    </ArrowLeftButton>
                                    <QuantityInput type="number" defaultValue="1" min="1" max="25"/>
                                    <ArrowRightButton>
                                        <img src={arrowRight} alt="arrow-right"/>
                                    </ArrowRightButton>
                                </QuantityContent>
                            </QuantityContainer>

                        </InfosContainer>
                    </Description>

                    <ProductBuyContainer>
                        <ProductBuyButton>
                            <StyledLink>
                                COMPRAR
                            </StyledLink>
                        </ProductBuyButton>

                        <ButtonCartContainer onClick={() => {
                            delCartProduct(id)
                        }}>
                            <ButtonCart src={remCartImg} alt="Cart Icon"/>
                        </ButtonCartContainer>

                    </ProductBuyContainer>
                </DescriptionContainer>

            </Product>
        </ProductsList>
    )
}

export default CartProductsStyled
