function CartProducts() {
    const [cartProducts, setCartProducts] = useState([]);

    async function fetchCartProducts() {
        const cartProductsAPI = await getCartProducts();
        setCartProducts(cartProductsAPI);
    }

    useEffect(() => {
        fetchCartProducts();
    }, []);

    function handleDeleteProduct(productId) {
        setCartProducts((prevProducts) =>
            prevProducts.filter((product) => product.id !== productId)
        );
    }

    // Função para garantir que valores com vírgula sejam convertidos corretamente
    const parsePrice = (price) => parseFloat(price.replace('.', '').replace(',', '.'));

    // Calcular os preços totais
    const totalPrice = cartProducts.reduce((total, product) => total + parsePrice(product.price), 0);
    const totalNewPrice = cartProducts.reduce((total, product) => total + parsePrice(product.newprice), 0);
    const totalDiscont = totalPrice - totalNewPrice;

    // Carregar as imagens
    const images = require.context('../assets/products-images', false, /\.(png|jpe?g|gif)$/);

    if (cartProducts.length < 1) {
        return (
            <CartEmptyContainer>
                <CartEmptyText>
                    <h1>Seu carrinho está vazio.</h1>
                    <p>Adicione algum produto para finalizar a compra</p>
                </CartEmptyText>

                <StyledLink to={"/"}>
                    <button>
                        VOLTE A COMPRAR
                    </button>
                </StyledLink>
            </CartEmptyContainer>
        );
    } else {
        return (
            <CartContainer>
                <ProductCard>
                    {cartProducts.map((product) => {
                        const imagePath = `./${product.src}.png`;
                        const image = images(imagePath);

                        return (
                            <CartProductsStyled
                                key={product.id}
                                name={product.name}
                                image={image}
                                price={product.price}
                                newprice={product.newprice}
                                id={product.id}
                                src={product.src}
                                onDelete={handleDeleteProduct}
                            />
                        );
                    })}
                </ProductCard>

                <Checkout>
                    <CheckoutTitle>RESUMO</CheckoutTitle>

                    <SummaryContainer>
                        <div>
                            <p>Total dos produtos:</p>
                            <p>R$ {parseFloat(totalPrice.toFixed(2)).toLocaleString('pt-BR')}</p>
                        </div>

                        <div>
                            <p>Frete:</p>
                            <p>R$ 0,00</p>
                        </div>
                    </SummaryContainer>

                    <TotalPrices>
                        <div>
                            <p>Total à vista:</p>
                            <p>R$ {parseFloat(totalNewPrice.toFixed(2)).toLocaleString('pt-BR')}</p>
                        </div>
                        <p>Desconto: R$ {parseFloat(totalDiscont.toFixed(2)).toLocaleString('pt-BR')}</p>
                    </TotalPrices>
                </Checkout>
            </CartContainer>
        );
    }
}

export default CartProducts;
