import styled from "styled-components";
import emailIcon from "../images/email-icon.png"

const FooterContainer = styled.footer`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    height: 10vh;
    width: 100%;
`

const FooterNewsContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    padding: 1rem;
    align-items: center;
    background-color: rgba(46,0,78,0.5);
    gap: 1rem;
    height: 50%;
    width: 100%;
`

const FooterNewsTitle = styled.h3`
    font-family: 'Poppins', sans-serif;
    font-weight: bold;
    font-size: 1.2rem;
    color: white;
`

const FooterNewsForm = styled.form`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    height: 100%;
    gap: 2rem;
`

const FooterNewsEmail = styled.input`
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: rgba(109, 0, 156, 0.5);;
    border: none;
    border-radius: .5rem;
    height: 60%;
    width: 20rem;
    color: white;
    fonte-weight: bold;
    padding: .5rem;

    &::placeholder {
        color: white;
    }

    &:focus-visible {
        outline: none;
    }
`

const FooterNewsButton = styled.button`
    display: flex;
    justify-content: center;
    height: 100%;
    align-items: center;
    font-weight: bold;
    background-color: rgba(109, 0, 156, 0.5);;
    border: none;
    border-radius: .5rem;
    color: white;
    padding: 1rem;
    cursor: pointer;

    &:hover {
        background: linear-gradient(315deg, rgba(46,0,78,0.5) 30%, rgba(125,0,180,0.5) 100%);
    }
`

const FooterText = styled.p`
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
    font-size: 1rem;
`


function Footer(){
    return(
        <FooterContainer>
            <FooterNewsContainer>
                <FooterNewsTitle>Receba ofertas</FooterNewsTitle>
                <FooterNewsForm>
                    <FooterNewsEmail type="email" placeholder="Email" />
                    <FooterNewsButton type="submit">Inscrever-se</FooterNewsButton>
                </FooterNewsForm>
            </FooterNewsContainer>

            <FooterText>Copyright Â© 2024 SevenShop</FooterText>
        </FooterContainer>
    )
}

export default Footer;
